package pkg1;

public class ClassD {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=10;
int b=30;
int c= a+b;
int d= a-b;
int e= a*b;
int f= a/b;
int g=b%a; 

//int hh= a--;
  a=a+1; //a++
  System.out.println(a);
  a=a-1; //a--
  System.out.println(a);
  
  System.out.println(c);
  System.out.println(d);
  System.out.println(e);
  System.out.println(f);
  System.out.println(g);
  System.out.println(--a);
  System.out.println(++a);
  
  
	}

}
